import React from "react";
import MenuList from "../components/menus/MenuList";
import { getAllMenus } from "../utils/local-data";

function HomePage() {
  const [menus, setMenus] = React.useState([]);
  const [title, setTitle] = React.useState(null);
  const [description, setDesc] = React.useState(null);
  const [imageUrl, setImageUrl] = React.useState(null);

  React.useEffect(() => {
    async function setAllMenus() {
      const { error, data } = await getAllMenus();

      if (!error) {
        setMenus(data);
      }
      setTitle(title);
      setDesc(description);
      setImageUrl(imageUrl);
    }
    setAllMenus();
    return () => {
      setTitle(null);
      setDesc(null);
      setImageUrl(null);
    };
  }, [title, description, imageUrl]);

  const menud = menus.filter((menu) => menu.title.toLowerCase());
  return (
    <div>
      <MenuList menus={menud} />
    </div>
  );
}

export default HomePage;
