import React from "react";
import Recomendation from "../components/sections/Recomendation";
import SectionLove from "../components/sections/SectionLove";
import Masakan from "../components/sections/Masakan";
import Cabang from "../components/sections/Cabang";
import Jabar from "../components/sections/Jabar";
import { getAllMenus } from "../utils/local-data";

function HomePage() {
  const [menus, setMenus] = React.useState([]);
  const [title, setTitle] = React.useState(null);
  const [description, setDesc] = React.useState(null);
  const [imageUrl, setImageUrl] = React.useState(null);

  React.useEffect(() => {
    async function setAllMenus() {
      const { error, data } = await getAllMenus();

      if (!error) {
        setMenus(data);
      }
      setTitle(title);
      setDesc(description);
      setImageUrl(imageUrl);
    }
  });

  const filterMenus = React.useMemo(() =>
    menus?.filter((menu) => {
      return menu.title.toLowerCase().includes();
    })
  );

  return (
    <div>
      <SectionLove />
      <Recomendation />
      <Masakan />
      <Cabang />
      <Jabar />
    </div>
  );
}

export default HomePage;
