import React from "react";
import { Navigate, useNavigate } from "react-router-dom";
import asinan from "../assets/image/asinan-bogor.jpg";

function CartPage() {
  const navigate = useNavigate;
  const onCheckout = () => {
    navigate("/payment");
  };
  return (
    <>
      <section>
        <table class="table">
          <thead>
            <tr>
              <th scope="col">No</th>
              <th scope="col">Menu</th>
              <th scope="col">Price</th>
              <th scope="col">Quantity</th>

              <th scope="col">Total</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              <td>
                <img
                  class="rounded-pill"
                  style={{ width: "100px" }}
                  src={asinan}
                  alt="detail asinan"
                />
                <p class="mt-2">Asinan Bogor</p>
              </td>
              <td>Rp.20.000</td>
              <td>1</td>
              <td>Rp.28.000</td>
            </tr>
          </tbody>
        </table>
      </section>
      <section class="d-flex justify-content-end m-4">
        <button
          class="btn"
          onClick={onCheckout}
          style={{ backgroundColor: "#90bcab", color: "white" }}
        >
          Checkout
        </button>
      </section>
    </>
  );
}

export default CartPage;
