import React from "react";
import SearchPoetry from "./search-poetry/SearchPoetry";
import { getInitialData } from "./utils/index";

class PoetryApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      poetry: getInitialData(),
      search: "",
    };

    this.onSearchEventHandler = this.onSearchEventHandler(this);
  }

  onSearchEventHandler(title) {
    this.setState(() => {
      return {
        search: title,
      };
    });
  }

  render() {
    const poetry = this.state.poetry.filter((note) => {
      note.title.toLowerCase().includes(this.state.search.toLowerCase());
    });
    const daftarPoetry = poetry.filter((note) => {
      return note.archived === false;
    });
    return (
      <div className="poetry-app">
        <div className="poetry-header">
          <h1>Poetry</h1>
          <SearchPoetry onSearch={this.onSearchEventHandler} />
        </div>
        <div className="poetry-body">{daftarPoetry.length > 0}</div>
        <div className="poetry-action"></div>
      </div>
    );
  }
}

export default PoetryApp;
