import React from "react";
import { useParams } from "react-router-dom";
import { getMenu } from "../../utils/local-data";

function DetailMenu() {
  const { id } = useParams();
  const [menu, setMenu] = React.useState({});
  React.useEffect(() => {
    // async function getNoteDetail() {
    //   const { error, data } = await getNote(id);

    //   if (!error) {
    //     setNote(data);
    //   }
    // }

    // getNoteDetail();
    setMenu(getMenu(id));
  }, [id]);
  return (
    <>
      <div className="row align-items-md-stretch m-3">
        <div className="col-md-6">
          <img
            className="rounded-pill"
            src="../assets/image/asinan-bogor.jpg"
            alt="detail asinan"
          />
        </div>
        <div className="col-md-6">
          <div className="h-100 p-5 bg-light border rounded-3 d-flex justify-center flex-column">
            <h2>{title}</h2>
            <p>
              Asinan adalah sejenis makanan yang dibuat dengan cara pengacaran
              (melalui pengasinan dengan garam atau pengasaman dengan cuka),
              bahan yang diacarkan yaitu berbagai jenis sayuran dan buah-buahan.
              Di Indonesia, asinan menjadi salah satu hidangan khas seni kuliner
              Indonesia.
            </p>
            <div className="d-flex">
              <p className="me-3">Rp.20.000 x</p>
              <input
                className="mt-1"
                type="text"
                placeholder=" 1 "
                style={{ height: "3vh", width: "5vh" }}
              />
            </div>
            <button
              onclick="tambah()"
              className="btn btn-outline-secondary h-15 w-50"
              type="button"
            >
              Tambahkan Keranjang
            </button>
            <button
              className="btn h-15 w-50"
              onclick="pesan()"
              style={{ backgroundColor: "#90bcab", color: "white" }}
              type="button"
            >
              Pesan Sekarang
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default DetailMenu;
